import React, { useEffect, useState } from 'react';
import { socket } from '../socket';
import axios from 'axios';

const Chat = () => {
  const [message, setMessage] = useState('');
  const [chatLog, setChatLog] = useState([]);
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);

  const token = localStorage.getItem('token');

  console.log(token,"token")
  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    // Fetch all other users
    axios.get('http://localhost:5000/api/auth/users', {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    }).then(res => setUsers(res.data));
  }, []);

  useEffect(() => {
    socket.auth = { token };
    socket.connect();

    socket.on('receiveMessage', (data) => {
      if (data.sender === selectedUser?._id) {
        setChatLog(prev => [...prev, data]);
      }
    });

    return () => socket.disconnect();
  }, [selectedUser]);

  const sendMessage = () => {
    if (message.trim() && selectedUser) {
      socket.emit('sendMessage', { to: selectedUser._id, message });
      setChatLog(prev => [...prev, { sender: user._id, message }]);
      setMessage('');
    }
  };

  return (
    <div>
      <h3>Welcome, {user.username}</h3>

      <div>
        <label>Select a user to chat with:</label>
        <select onChange={e => {
          const selected = users.find(u => u._id === e.target.value);
          setSelectedUser(selected);
          setChatLog([]); // Reset chat for new user
        }}>
          <option value="">-- Select User --</option>
          {users.map(u => (
            <option key={u._id} value={u._id}>{u.username}</option>
          ))}
        </select>
      </div>

      {selectedUser && (
        <>
          <h4>Chatting with: {selectedUser.username}</h4>
          <div style={{ border: '1px solid black', padding: 10, height: 200, overflowY: 'scroll' }}>
            {chatLog.map((msg, i) => (
              <div key={i} style={{ textAlign: msg.sender === user._id ? 'right' : 'left' }}>
                <strong>{msg.sender === user._id ? 'You' : selectedUser.username}:</strong> {msg.message}
              </div>
            ))}
          </div>
          <input value={message} onChange={e => setMessage(e.target.value)} placeholder="Type a message..." />
          <button onClick={sendMessage}>Send</button>
        </>
      )}
    </div>
  );
};

export default Chat;
